import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import Loginen from "./Locales/en/Loginen.json";
import Loginjp from "./Locales/jp/Loginjp.json";

const resources = {
  en: {
    login: Loginen,
  },
  jp: {
    login: Loginjp,
  },
};
i18n
  .use(LanguageDetector)
  .use(initReactI18next)

  .init({
    // debug: true,
    // lang: "jp",
    // language: "jp",
    // fallbackLng: "jp",
    // interpolation: {
    //   escapeValue: false,
    // },
    fallbackLng: "en",
    lang: "en",
    language: "en",
    async: true,
    debug: false,
    react: {
      // wait: true,
      useSuspense: true,
    },
    languageOptions: ["en", "jp", "cn", "vm"],
    whitelist: ["en", "jp", "cn", "vm"],
    saveMissing: true,
    saveMissingTo: "all",
    keySeparator: true,
    ns: ["translation"],
    defaultNS: "translation",
    interpolation: {
      escapeValue: false,
    },
    resources: resources,
  });

export default i18n;
