import React, { Component } from "react";
//import logo from "./logo.svg";
import "./App.css";
import { withTranslation } from "react-i18next";
import i18n from "i18next";
class App extends Component {
  changeLanguageToggle = (lang = "en") => {
    const { open } = this.state;

    /** Force the language update on callback **/
    i18n.changeLanguage(lang, () => {
      this.getActiveLanguageMap(i18n.language);
      this.setState({ open: !open });
    });
  };
  render() {
    console.log(i18n.options.language);
    let { language } = i18n.options.languageOptions;
    return (
      <div className="App">
        <header className="App-header">
          {/* <img src={logo} className="App-logo" alt="logo" /> */}
          <p>{i18n.t("login:title")}</p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            {i18n.t("login:description.part1")}
          </a>
          <div>{i18n.language}</div>
          {language.map((lang, key) => {
            return <div>{i18n.t("language." + lang)}</div>;
          })}
        </header>
      </div>
    );
  }
}

export default withTranslation()(App);
