import React, { Component } from "react";
//import logo from "./logo.svg";
import "./App.css";
import { withTranslation } from "react-i18next";
import i18n from "i18next";
class App extends Component {
  changeLanguageToggle = (lang = "en") => {
    /** Force the language update on callback **/
    i18n.changeLanguage(lang);
  };
  // getActiveLanguageMap = (language) => {
  //   let activeLang;

  //   i18n.options.languageOptions.forEach((lang, key) => {
  //     if (lang === language) {
  //       activeLang = i18n.t("language." + lang);
  //     }
  //   });

  //   return activeLang;
  // };
  render() {
    return (
      <div className="App">
        <header className="App-header">
          {/* <img src={logo} className="App-logo" alt="logo" /> */}
          <p>{i18n.t("login:title")}</p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            {i18n.t("login:description.part1")}
          </a>
          <div>{i18n.language}</div>
          {i18n.options.languageOptions.map((lang, key) => {
            return (
              <div onClick={() => this.changeLanguageToggle(lang)}>{lang}</div>
            );
          })}
        </header>
      </div>
    );
  }
}

export default withTranslation()(App);
